from enum import Enum
from typing import cast

from loguru import logger

from langflow.custom import Component
from langflow.io import DataInput, DropdownInput, Output
from langflow.schema import DataFrame


class DataOperation(str, Enum):
    CONCATENATE = "Concatenate"
    APPEND = "Append"
    MERGE = "Merge"
    JOIN = "Join"


class MergeDataComponent(Component):
    display_name = "Combine Data"
    description = "Combines data using different operations"
    icon = "merge"
    MIN_INPUTS_REQUIRED = 2

    inputs = [
        DataInput(name="data_inputs", display_name="Data Inputs", info="Data to combine", is_list=True, required=True),
        DropdownInput(
            name="operation",
            display_name="Operation Type",
            options=[op.value for op in DataOperation],
            value=DataOperation.CONCATENATE.value,
        ),
    ]
    outputs = [Output(display_name="DataFrame", name="combined_data", method="combine_data")]

    def combine_data(self) -> DataFrame:
        if not self.data_inputs or len(self.data_inputs) < self.MIN_INPUTS_REQUIRED:
            empty_dataframe = DataFrame()
            self.status = empty_dataframe
            return empty_dataframe

        operation = DataOperation(self.operation)
        try:
            combined_dataframe = self._process_operation(operation)
            self.status = combined_dataframe
        except Exception as e:
            logger.error(f"Error during operation {operation}: {e!s}")
            raise
        else:
            return combined_dataframe

    def _process_operation(self, operation: DataOperation) -> DataFrame:
        if operation == DataOperation.CONCATENATE:
            combined_data: dict[str, str | object] = {}
            for data_input in self.data_inputs:
                for key, value in data_input.data.items():
                    if key in combined_data:
                        if isinstance(combined_data[key], str) and isinstance(value, str):
                            combined_data[key] = f"{combined_data[key]}\n{value}"
                        else:
                            combined_data[key] = value
                    else:
                        combined_data[key] = value
            return DataFrame([combined_data])

        if operation == DataOperation.APPEND:
            rows = [data_input.data for data_input in self.data_inputs]
            return DataFrame(rows)

        if operation == DataOperation.MERGE:
            result_data: dict[str, str | list[str] | object] = {}
            for data_input in self.data_inputs:
                for key, value in data_input.data.items():
                    if key in result_data and isinstance(value, str):
                        if isinstance(result_data[key], list):
                            cast("list[str]", result_data[key]).append(value)
                        else:
                            result_data[key] = [result_data[key], value]
                    else:
                        result_data[key] = value
            return DataFrame([result_data])

        if operation == DataOperation.JOIN:
            combined_data = {}
            for idx, data_input in enumerate(self.data_inputs, 1):
                for key, value in data_input.data.items():
                    new_key = f"{key}_doc{idx}" if idx > 1 else key
                    combined_data[new_key] = value
            return DataFrame([combined_data])

        return DataFrame()

from langflow.custom import Component
from langflow.io import MessageInput, Output
from langflow.schema import Data
from langflow.schema.content_types import MediaContent, TextContent
from langflow.schema.message import Message
from langflow.schema.content_block import ContentBlock
import subprocess
import os
import time
import json
import re

class LatexToPdfComponent(Component):
    display_name = "LaTeX to PDF & DOC"
    description = "Convert LaTeX code from an agent message to PDF or DOCX documents"
    icon = "file-text"
    name = "LatexToPdf"

    BUILD_DIR = "./latex_output"

    inputs = [
        MessageInput(
            name="message",
            display_name="LaTeX Message",
            info="Agent message stream containing LaTeX source code",
            required=True,
        )
    ]

    outputs = [
        Output(
            name="pdf_message",
            display_name="PDF Preview Message",
            method="build_pdf",
            info="Chat message with embedded PDF preview"
        ),
        Output(
            name="docx_message",
            display_name="DOCX Preview Message",
            method="build_doc",
            info="Chat message with embedded DOCX preview"
        ),
        Output(
            name="resume_out",
            display_name="Resume Output in Text",
            method="return_message",
            info="Text extracted from PDF"
        )
    ]

    def _extract_latex(self):
        raw = self.message.content if hasattr(self.message, 'content') else str(self.message)
        data = json.loads(raw)
        tex = data["text"]
        tex = re.sub(r'^```[a-zA-Z]*\n', '', tex)
        tex = re.sub(r'\n```$', '', tex)
        return tex

    def build_pdf(self) -> Message:
        os.makedirs(self.BUILD_DIR, exist_ok=True)
        tex_path = os.path.join(self.BUILD_DIR, "resume.tex")
        pdf_path = os.path.join(self.BUILD_DIR, "resume.pdf")
        png_path = os.path.join(self.BUILD_DIR, "resume")

        with open(tex_path, "w") as f:
            f.write(self._extract_latex())

        try:
            subprocess.run(
                ["xelatex", "-interaction=batchmode", f"-output-directory={self.BUILD_DIR}", tex_path],
                check=True, stdout=subprocess.DEVNULL, stderr=subprocess.DEVNULL
            )
        except subprocess.CalledProcessError as e:
            self.status = "❌ LaTeX compilation failed"
            return Data(content=TextContent(text=f"LaTeX error: {e}"))

        try:
            subprocess.run(["pdftocairo", "-png", "-singlefile", pdf_path, png_path], check=True)
        except subprocess.CalledProcessError as e:
            self.status = "❌ PDF to PNG conversion failed"
            return Data(content=TextContent(text=f"pdftocairo error: {e}"))

        try:
            subprocess.run(["pdftotext", pdf_path, os.path.join(self.BUILD_DIR, "resume")], check=True)
        except subprocess.CalledProcessError as e:
            self.status = "❌ PDF to Text conversion failed"
            return Data(content=TextContent(text=f"pdftotext error: {e}"))

        with open(os.path.join(self.BUILD_DIR, "resume"), "r") as f:
            extracted_text = f.read()

        self.status = "✅ PDF generated and accessible"
        return Message(
            text="Resume PDF Generated",
            sender="User",
            sender_name="John Doe",
            content_blocks=[
                ContentBlock(
                    title="📄 Resume Extracted Text",
                    contents=[TextContent(type="text", text=extracted_text)]
                ),
                ContentBlock(
                    title="📥 Download PDF",
                    contents=[
                        TextContent(
                            type="text",
                            text=f'<a href="http://localhost:8888/{os.path.basename(pdf_path)}" download target="_blank">Download PDF</a>'
                        )
                    ]
                ),
                ContentBlock(
                    title="📷 PDF Preview",
                    contents=[MediaContent(type="media", urls=[
                        f"http://localhost:8888/{os.path.basename(png_path)}.png",
                        f"http://localhost:8888/{os.path.basename(pdf_path)}"
                    ])]
                )
            ]
        )

    def build_doc(self) -> Message:
        os.makedirs(self.BUILD_DIR, exist_ok=True)
        tex_path = os.path.join(self.BUILD_DIR, "resume.tex")
        docx_path = os.path.join(self.BUILD_DIR, "resume.docx")

        with open(tex_path, "w") as f:
            f.write(self._extract_latex())

        try:
            subprocess.run(
                ["pandoc", tex_path, "-o", docx_path],
                check=True, stdout=subprocess.DEVNULL, stderr=subprocess.DEVNULL
            )
        except subprocess.CalledProcessError as e:
            self.status = "❌ DOCX conversion failed"
            return Data(content=TextContent(text=f"Pandoc error: {e}"))

        self.status = "✅ DOCX generated and accessible"
        return Message(
            text="Resume DOCX Generated",
            sender="User",
            sender_name="John Doe",
            content_blocks=[
                ContentBlock(
                    title="📥 Download DOCX",
                    contents=[
                        TextContent(
                            type="text",
                            text=f'<a href="http://localhost:8888/{os.path.basename(docx_path)}" download target="_blank">Download DOCX</a>'
                        )
                    ]
                ),
                ContentBlock(
                    title="📷 DOCX Preview",
                    contents=[MediaContent(type="media", urls=[
                        f"http://localhost:8888/{os.path.basename(docx_path)}"
                    ])]
                )
            ]
        )

    def return_message(self) -> Message:
        time.sleep(1)
        text_file = os.path.join(self.BUILD_DIR, "resume")
        try:
            with open(text_file, "r") as f:
                return Message(text=f.read())
        except FileNotFoundError:
            return Message(text="❌ resume text file not found.")

